import axios from 'axios';

const BLD_API_BASE_URL = "http://localhost:8080/api/v1/employees";

class BuildingService {

    getEmployees(){
        //const data =axios.get(BLD_API_BASE_URL);
        return axios.get(BLD_API_BASE_URL);
        //return data;
    }

    createEmployee(employee){
        return axios.post(BLD_API_BASE_URL, employee);
    }

    getEmployeeById(employeeId){
        return axios.get(BLD_API_BASE_URL + '/' + employeeId);
    }

    updateEmployee(employee, employeeId){
        return axios.put(BLD_API_BASE_URL + '/' + employeeId, employee);
    }

    deleteEmployee(employeeId){
        return axios.delete(BLD_API_BASE_URL + '/' + employeeId);
    }
}

export default new EmployeeService()
